import React, { useEffect, useState } from 'react';
import './App.css';
import LemonImage from './image/lemon.svg';
import LemonAudio from './audio/lemon.mp3';

function App() {
  const[audio, setAudio] = useState<boolean>(false);
  const lemon = new Audio(LemonAudio);

  const play = () => {
    lemon.play();
    lemon.currentTime = 2;
  }

  const stop = () => {
    lemon.pause();
    lemon.currentTime = 0;
  };

  return (
    <main className='flex'>
      <div className='flex-1 bg-slate-100'>
        <img className='cursor-pointer py-5 px-5' src={LemonImage} alt='LemonImage' width={300} />
        <div className='flex'>
          <button className='flex-1 mx-5 text-white bg-gray-700 border-solid border-white rounded-md' onClick={() => play()}>再生</button>
          <button className='flex-1 text-white bg-gray-700 border-solid border-white rounded-md' onClick={() => lemon.pause()}>一時停止</button>
          <button className='flex-1 text-white bg-gray-700 border-solid border-white rounded-md' onClick={() => stop()}>最初に戻す</button>
        </div>
      </div>
      <div className='flex-1 bg-slate-100'>
        <img className='cursor-pointer py-5 px-5' src={LemonImage} alt='LemonImage' width={300} />
        <div className='flex'>
          <button className='flex-1 text-white bg-gray-700 border-solid border-white rounded-md' onClick={() => play()}>再生</button>
          <button className='flex-1 text-white bg-gray-700 border-solid border-white rounded-md' onClick={() => lemon.pause()}>一時停止</button>
          <button className='flex-1 text-white bg-gray-700 border-solid border-white rounded-md' onClick={() => stop()}>最初に戻す</button>
        </div>
      </div>
    </main>
  );
}

export default App;
