import React, { useEffect, useState } from 'react';
import './App.css';
import LemonImage from './image/lemon.svg';
import LemonAudio from './audio/lemon.mp3';

function App() {
  const[audio, setAudio] = useState<boolean>(false);
  const lemon = new Audio(LemonAudio);

  const play = () => {
    lemon.play();
    lemon.currentTime = 1;
  }

  const stop = () => {
    lemon.pause();
    lemon.currentTime = 0;
  };

  return (
    <>
      <img className='cursor-pointer text-center' src={LemonImage} alt='LemonImage' width={300} />
      <div className='flex'>
        <button className='flex-1' onClick={() => play()}>再生</button>
        <button className='flex-1' onClick={() => lemon.pause()}>一時停止</button>
        <button className='flex-1' onClick={() => stop()}>最初に戻す</button>
      </div>
    </>
  );
}

export default App;
