import React, { useEffect, useState } from 'react';
import './App.css';
import LemonImage from './image/lemon.svg';
import LemonAudio from './audio/lemon.mp3';

function App() {
  const[audio, setAudio] = useState<boolean>(true);
  const lemon = new Audio(LemonAudio);

  const play = () => {
    setAudio(!audio);
    console.log(audio)
  };

  useEffect(() => {
    console.log('test')
    audio ? (
      lemon.play()
    ) : (
      lemon.pause(),
      lemon.currentTime = 0
    );
  }, [audio]);

  return (
    <>
      <button onClick={play}><img className='cursor-pointer' src={LemonImage} alt='LemonImage' width={300} /></button>
    </>
  );
}

export default App;
