  const[audio, setAudio] = useState<boolean>(false);
  const lemon = new Audio(LemonAudio);

  return (
    <>
      <img className='cursor-pointer' src={LemonImage} alt='LemonImage' width={300} />
      <button onClick={() => lemon.play()}>再生</button>
      <button onClick={() => lemon.play()}></button>
      <button onClick={() => lemon.play()}></button>
    </>
  );
}

export default App;
